mapper.select:
select 

,

from PS_TZ_MODAL_DT_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TREE_NAME = #{treeName,jdbcType=VARCHAR}
  and TZ_SCORE_ITEM_ID = #{tzScoreItemId,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_MODAL_DT_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TREE_NAME = #{treeName,jdbcType=VARCHAR}
  and TZ_SCORE_ITEM_ID = #{tzScoreItemId,jdbcType=VARCHAR}
