mapper.select:
select 

from PS_TZ_WXMSG_LOG_T
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_WX_APPID = #{tzWxAppid,jdbcType=VARCHAR}
  and TZ_XH = #{tzXh,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_WXMSG_LOG_T
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_WX_APPID = #{tzWxAppid,jdbcType=VARCHAR}
  and TZ_XH = #{tzXh,jdbcType=VARCHAR}
