mapper.select:
select 

from PS_TZ_WX_USER_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_WX_APPID = #{tzWxAppid,jdbcType=VARCHAR}
  and TZ_OPEN_ID = #{tzOpenId,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_WX_USER_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_WX_APPID = #{tzWxAppid,jdbcType=VARCHAR}
  and TZ_OPEN_ID = #{tzOpenId,jdbcType=VARCHAR}
